import { MergedParserOutput } from "./utils";
import { ParserOutput } from "@sap-ux/vocabularies-types";

export function merge(parserOutputs: ParserOutput[]): ParserOutput {
	const outParserOutput = new MergedParserOutput(parserOutputs[0]);

	parserOutputs.forEach((parserOutput: ParserOutput) => {
		outParserOutput.addParserOutput(parserOutput);
	});

	return outParserOutput;
}
