import * as Core from "./Core";
import * as Common from "./Common";
import * as Edm from "../Edm";
import AnnotationTerm = Edm.AnnotationTerm;
import PropertyAnnotationValue = Edm.PropertyAnnotationValue;
import EnumValue = Edm.EnumValue;
import ComplexType = Edm.ComplexType;

// Term
/**
  CSS definitions that may be used as defaults
*/
export type CssDefaults = AnnotationTerm<CssDefaultsTypeTypes>;

// ComplexType
export type CssDefaultsType = ComplexType & {
	$Type: HTML5AnnotationTypes.CssDefaultsType;
	/**
        css: width, see https://www.w3.org/TR/CSS21/visudet.html#propdef-width
    */
	width?: PropertyAnnotationValue<Edm.String>;
};

export const enum HTML5AnnotationTerms {
	CssDefaults = "com.sap.vocabularies.HTML5.v1.CssDefaults"
}
export const enum HTML5AnnotationTypes {
	CssDefaultsType = "com.sap.vocabularies.HTML5.v1.CssDefaultsType"
}

export type CssDefaultsTypeTypes = AnnotationTerm<CssDefaultsType>;
